+}
+
+.producto-detalle-precio {
+    font-size: 1.8rem;
+    font-weight: 700;
+    color: #e74c3c;
+    margin-bottom: 1.5rem;
+}
+
+.producto-detalle-descripcion {
+    margin-bottom: 2rem;
+}
+
+.producto-detalle-descripcion h3 {
+    font-size: 1.2rem;
+    margin-bottom: 0.5rem;
+    color: #2c3e50;
+}
+
+.producto-detalle-descripcion p {
+    color: #7f8c8d;
+    line-height: 1.6;
+}
+
+.btn-comprar {
+    display: block;
+    background-color: #2ecc71;
+    color: #fff;
+    text-align: center;
+    padding: 1rem;
+    border-radius: 4px;
+    font-weight: 600;
+    font-size: 1.1rem;
+    transition: background-color 0.3s;
+    cursor: pointer;
+    border: none;
+    width: 100%;
+}
+
+.btn-comprar:hover {
+    background-color: #27ae60;
+}
+
+.close {
+    position: absolute;
+    top: 1rem;
+    right: 1rem;
+    font-size: 2rem;
+    color: #7f8c8d;
+    cursor: pointer;
+    transition: color 0.3s;
+    z-index: 10;
+}
+
+.close:hover {
+    color: #e74c3c;
+}
+
+/* Responsive para el modal */
+@media (max-width: 768px) {
+    .producto-detalle-content {
+        grid-template-columns: 1fr;
+    }
+    
+    .producto-detalle-imagen {
+        min-height: 300px;
+    }
+    
+    .producto-detalle-info {
